app.configure(() => {
  return [
    {
      key: 'emote',
      type: 'file',
      kind: 'emote',
      label: 'Emote',
    },
    {
      key: 'hover',
      type: 'switch',
      label: 'Hover',
      options: [
        { label: 'No', value: false },
        { label: 'Yes', value: true },
      ],
      initial: false,
    },
    {
      key: 'rotate',
      type: 'switch',
      label: 'Rotate',
      options: [
        { label: 'No', value: false },
        { label: 'Yes', value: true },
      ],
      initial: false,
    },
    {
      key: 'avatars',
      type: 'switch',
      label: 'Avatar',
      options: [
        { label: '1', value: '1' },
        { label: '2', value: '2' },
        { label: '3', value: '3' },
        { label: '4', value: '4' },
      ]
    },
    {
      key: 'autoCycle',
      type: 'switch',
      label: 'Auto Cycle',
      options: [
        { label: 'No', value: false },
        { label: 'Yes', value: true },
      ],
      initial: true,
    },
    ...customAvatarFields('1'),
    ...customAvatarFields('2'),
    ...customAvatarFields('3'),
    ...customAvatarFields('4'),
    {
      key: 'clear',
      type: 'button',
      label: 'Clear Avatar',
      onClick: () => {
        app.send('avatar:clear')
      }
    }
  ];

  function customAvatarFields(n) {
    return [
      {
        key: `avatar${n}_name`,
        type: 'text',
        label: 'Name',
        when: [{ key: 'avatars', op: 'eq', value: n }]
      },
      {
        key: `avatar${n}`,
        type: 'file',
        label: 'Avatar',
        kind: 'avatar',
        when: [{ key: 'avatars', op: 'eq', value: n }]
      },
    ]
  }
})

