  try {
    await fetch(`${props.relay}/beacon`, {
      method: 'PUT',
      body: JSON.stringify({
        url: props.url,
        name: props.name,
        description: props.description,
        active: true,
        image: props.image,
        adult: false,
        tags: props.tags,
      }),
      headers: {
        'Content-Type': 'application/json'
      }
