      kind: 'audio',
      label: 'Explosion Sound'
    }
])

// Initialize app state
if (!app.state.confettiActive) {
    app.state.confettiActive = false
}

// Force reset confetti active state on startup
if (world.isClient) {
    app.state.confettiActive = false
}

if (world.isServer) {
    app.state.confettiActive = false
    app.send('setConfettiActive', false)
}

