const createGrid = () => {
    // Clear existing objects
    objects.forEach(obj => {
        app.remove(obj.node)
    })
    objects.length = 0

    if (isCircular) {
        // Create circular grid
        for (let x = -GRID_SIZE/2; x < GRID_SIZE/2; x++) {
            for (let z = -GRID_SIZE/2; z < GRID_SIZE/2; z++) {
                const distance = Math.sqrt(x * x + z * z)
                if (distance > RADIUS) continue

                createObject(x, z)
            }
        }
    } else {
        // Create square grid - updated to match circular grid coordinates
        for (let x = -GRID_SIZE/2; x < GRID_SIZE/2; x++) {
            for (let z = -GRID_SIZE/2; z < GRID_SIZE/2; z++) {
                createObject(x, z)
            }
        }
    }
}

const createObject = (x, z) => {
    const obj = template.clone(true)
    obj.visible = true
    obj.scale.set(0.1, 0.1, 0.1)

    const offsetX = x * SPACING
    const offsetZ = z * SPACING
    obj.position.set(offsetX, 0, offsetZ)  // Removed the grid offset since x,z are already centered

    objects.push({
        node: obj,
        baseY: obj.position.y,
        x: x,
        z: z
    })

    app.add(obj)
}

