const NUM_EMOTES = 6

const fields = [
  {
    key: 'icon',
    type: 'image',
    label: 'Button Icon',
    // hidden: true,
  },
  {
    key: 'hide',
    type: 'toggle',
    label: 'Hide',
  }
]

for (let i = 0; i < NUM_EMOTES; i++) {
  fields.push({
    key: `section${i}`,
    type: 'section',
    label: `Emote`
  })
  fields.push({
    key: `emote${i}`,
    type: 'emote',
    label: `File`
  })
  fields.push({
    key: `label${i}`,
    type: 'text',
    label: `Label`
  })
  fields.push({
    key: `icon${i}`,
    type: 'image',
    label: `Icon`
  })
  fields.push({
    key: `gaze${i}`,
    type: 'toggle',
    label: `Gaze`
  })
}

app.configure(fields)

if (config.hide) {
  const block = app.get('Block')
  app.remove(block)
}

