  height: 50,
  backgroundColor: 'rgba(0, 0, 0, 0.3)',
  borderRadius: 25,
  pivot: 'bottom-right',
  position: [1, 1],
  offset: [-30, -120],
  cursor: 'pointer',
  onPointerDown: toggle,
  alignItems: 'center',
  justifyContent: 'center',
})
const icon = app.create('uiimage', {
  width: 20,
  height: 20,
  src: config.icon?.url,
})
btn.add(icon)
