  ORBIT_RADIUS: 5,
  ORBIT_HEIGHT: 2,
  ORBIT_MOVE_SPEED: 0.3,
  ORBIT_MOVE_LERP: 0.2,
  ORBIT_LOOK_SPEED: 0.9,
  ORBIT_LOOK_LERP: 0.2,
  ORBIT_TARGET_MOVE_SPEED: 1.0,

  // Freecam parameters (normalized speed 0-1)
  FREECAM_MOVE_SPEED: 0.3,
  FREECAM_MOVE_LERP: 0.05,
  FREECAM_LOOK_SPEED: 0.5,
