  FREECAM_LOOK_LERP: { min: 0.01, max: 0.25 },
  ORBIT_MOVE_LERP: { min: 0.01, max: 0.25 },
  ORBIT_LOOK_LERP: { min: 0.01, max: 0.25 }
}

app.keepActive = true

