// Ancient Roman City — Hyperfy script
// Performance-conscious, static colliders with some kinematic actors

// Randomness (deterministic)
const rng = prng(1066);
const R = (min, max, dp = 0) => rng(min, max, dp);
const deg = (a) => (a * Math.PI) / 180;

// Root
const city = app.create('group');
app.add(city);

// Materials/Palette
const MAT = {
  travertine: '#e8dcc8',
  marble: '#f2f2f2',
  plaster: '#f7f1e7',
  tile: '#b23a34',
  wood: '#7a4a2e',
  bronze: '#8a6f3b',
  stone: '#cbb8a0',
  basalt: '#2c2c2c',
  water: '#4fa3ff',
  greenery: '#4a7a3a',
  sand: '#d8c8a8',
  brick: '#a55233',
  shadow: '#808080',
};

// Helpers
const add = (node, parent = city) => {
  parent.add(node);
  return node;
};

const makeBox = (
  w,
  h,
  d,
  color = MAT.stone,
  physics = 'static',
  y = h / 2
) => {
  return app.create('prim', {
    type: 'box',
    size: [w, h, d],
    color,
    physics,
    position: [0, y, 0],
  });
};

const makePlaneXZ = (w, d, color, y = 0, physics = 'static') => {
  const p = app.create('prim', {
    type: 'plane',
    size: [w, d],
    color,
    physics,
  });
  p.rotation.x = -Math.PI / 2;
  p.position.y = y;
  return p;
};

const makeCylinder = (
  rTop,
  rBtm,
  h,
  color = MAT.stone,
  physics = 'static',
  y = h / 2
) => {
  return app.create('prim', {
    type: 'cylinder',
    size: [rTop, rBtm, h],
    color,
    physics,
    position: [0, y, 0],
  });
};

const makeCone = (r, h, color = MAT.tile, physics = 'static', y = h / 2) => {
  return app.create('prim', {
    type: 'cone',
    size: [r, h],
    color,
    physics,
    position: [0, y, 0],
  });
};

const makeTorus = (radius, tube, color = MAT.bronze, physics = 'static') => {
  return app.create('prim', {
    type: 'torus',
    size: [radius, tube],
    color,
    physics,
    position: [0, 0, 0],
  });
};

const setPos = (node, x, y, z) => {
  node.position.set(x, y, z);
  return node;
};

const setRot = (node, x, y, z) => {
  node.rotation.set(x, y, z);
  return node;
};

