export default function main(world, app, fetch, props, setTimeout) {
// Configurable image display with refresh functionality
// - Allows customization of chart URL and display settings

// Configure the app's customization fields
app.configure([
  // Section for Chart Settings
  {
    type: 'section',
    key: 'chartSection',
    label: 'Chart Settings'
  },
  {
    type: 'text',
    key: 'chartUrl',
    label: 'Chart URL',
    placeholder: 'Enter chart URL',
    initial: 'https://cambrian.rickydata.com/plot_best_categories'
  },
  {
    type: 'text',
    key: 'refreshLabel',
    label: 'Refresh Button Label',
    placeholder: 'Enter refresh button label',
    initial: 'Refresh Chart [E]'
  },
  {
    type: 'range',
    key: 'refreshRadius',
    label: 'Interaction Radius (meters)',
    min: 1,
    max: 10,
    step: 0.5,
    initial: 5
  },

  // Section for Display Settings
  {
    type: 'section',
    key: 'displaySection',
    label: 'Display Settings'
  },
  {
    type: 'range',
    key: 'width',
    label: 'Chart Width',
    min: 300,
    max: 1200,
    step: 10,
    initial: 640
  },
  {
    type: 'range',
    key: 'height',
    label: 'Chart Height',
    min: 200,
    max: 800,
    step: 10,
    initial: 480
  },
  {
    type: 'range',
    key: 'posX',
    label: 'Position X',
    min: -10,
    max: 10,
    step: 0.1,
    initial: 0
  },
  {
    type: 'range',
    key: 'posY',
    label: 'Position Y (Height)',
    min: 0,
    max: 5,
    step: 0.1,
    initial: 1.7
  },
  {
    type: 'range',
    key: 'posZ',
    label: 'Position Z',
    min: -10,
    max: 10,
    step: 0.1,
    initial: -3
  },
  {
    type: 'dropdown',
    key: 'imageFit',
    label: 'Image Fit',
    options: [
      { label: 'Contain', value: 'contain' },
      { label: 'Cover', value: 'cover' },
      { label: 'Fill', value: 'fill' },
      { label: 'None', value: 'none' },
      { label: 'Scale-down', value: 'scale-down' }
    ],
    initial: 'fill'
  }
]);

// Position from config
con
