// Configure the app's customization fields
app.configure([
  // Section for Text
  {
    type: 'section',
    key: 'textSection',
    label: 'Action Trigger'
  },
  {
    type: 'text',
    key: 'showLabel',
    label: 'Show Label',
    placeholder: 'Enter show label',
    initial: 'Read'
  },
  {
    type: 'text',
    key: 'closeLabel',
    label: 'Close Label',
    placeholder: 'Enter close label',
    initial: 'Close'
  },

  // Section for UI Dimensions
  {
    type: 'section',
    key: 'uiDimensionsSection',
    label: 'UI Dimensions'
  },
  {
    type: 'range',
    key: 'uiWidth',
    label: 'UI Width',
    min: 100,
    max: 500,
    step: 1,
    initial: 200
  },
  {
    type: 'range',
    key: 'uiHeight',
    label: 'UI Height',
    min: 100,
    max: 500,
    step: 1,
    initial: 150
  },

  // Section for UI Image
  {
    type: 'section',
    key: 'uiImageSection',
    label: 'UI Image'
  },
  {
    type: 'text',
    key: 'imageSrc',
    label: 'Image Source URL',
    placeholder: 'Enter image URL',
    initial: 'https://via.placeholder.com/100'
  },
  {
    type: 'range',
    key: 'imageWidth',
    label: 'Image Width',
    min: 50,
    max: 200,
    step: 1,
    initial: 100
  },
  {
    type: 'range',
    key: 'imageHeight',
    label: 'Image Height',
    min: 50,
    max: 200,
    step: 1,
    initial: 100
  },
  {
    type: 'dropdown',
    key: 'imageFit',
    label: 'Image Fit',
    options: [
      { label: 'Contain', value: 'contain' },
      { label: 'Cover', value: 'cover' },
      { label: 'Fill', value: 'fill' },
      { label: 'None', value: 'none' },
      { label: 'Scale-down', value: 'scale-down' }
    ],
    initial: 'contain'
  },
  {
    type: 'range',
    key: 'imageAlpha',
    label: 'Image Background Alpha',
    min: 0,
    max: 1,
    step: 0.01,
    initial: 1
  }
]);

