    text.color = '#ffffff'
    ui.add(text)

    // Position above target player
    ui.position.copy(targetPlayer.position)
    ui.position.y += 2.5

    return { ui, text }
  }

  function showEmoteIndicator(targetPlayer, emoji) {
    let emoteUI = activeEmoteUIs.get(targetPlayer.id)

