          if (prop in app) {
            return app[prop];
          }
          // Otherwise, fallback to app.get('<prop>')
          return app.get(prop);
        },
        set(target, prop, value, receiver) {
          // If app already has a property, assign directly.
          if (prop in app) {
            app[prop] = value;
            return true;
          }
          // Otherwise, if app has a setter function, use it.
          if (typeof app.set === 'function') {
            app.set(prop, value);
            return true;
          }
